import re
import sys

file_to_process = sys.argv[1]

#bad_icon = "data:image/png;base64,"

bad_icon = "data:image/png;base64,"

correct_icon = "data:image/png;base64,"

with open(file_to_process) as f:

    data = f.read()

data = data.replace(bad_icon, correct_icon)

with open(file_to_process, 'w') as f:

    f.write(data)
